


def isValid(s):
    # Write your code here

    dict = {}

    for char in s:
        if char in dict:
            dict[char] += 1
        else:
            dict[char] = 1
    print(dict)

    l1 = list(dict.values())
    set_len = len(set(l1))
    if set_len == 1:
        return("YES")

    if set_len ==2:
        maxi = max(set(l1))
        mini = min(set(l1))
        if maxi - mini == 1:
            if l1.count(maxi) ==1:
                return("YES")
            if l1.count(mini) ==1:
                return("YES")
        if mini ==1 and l1.count(mini) == 1:
            return("YES")
    return("NO")


print(isValid("abc"))
print(isValid("abcc"))
